tileentity.line:
var princess = getItemSlot(nbt.Items, 0);
if (!princess) {
    return RED + translate('for.errors.noQueen.desc');
}
/* Name */
var results = ['\\n'];
results.push(customLabelFormat(translate('hud.msg.Forestry.Queen.Name'), name(princess)));
/* health */
var health = princess.tag.Health;
var maxHealth = princess.tag.MaxH;
results.push(
    customLabelFormat(translate('hud.msg.Forestry.Queen.Health'), health + ' / ' + maxHealth)
);

return results.join('\\n  ');
