oo.init:
function getSlot(i, slot, slotName){
    slotName=slotName||"Slot";
    for (var index in i){
        if (i[index][slotName]==slot){
            return i[index];
            }
        }
}

tileentity.line:
var TagVis = getSlot(nbt['Inventory'], 10, 'Slot');
var rt = [];
rt.push(YELLOW + (TagVis['tag']['aer'] / 100).toFixed(1));
rt.push(DLIGHTBLUE + (TagVis['tag']['ignis'] / 100).toFixed(1));
rt.push(BLUE + (TagVis['tag']['aqua'] / 100).toFixed(1));
rt.push(LIGHTGRAY + (TagVis['tag']['ordo'] / 100).toFixed(1));
rt.push(DARKGRAY + (TagVis['tag']['perditio'] / 100).toFixed(1));
return rt.join(WHITE + ' | ' + RESET);

if (nbt['Inventory'][0]['Slot'] != 0) {
    return translate('hud.msg.Thaumcraft.BorePick') + ': ' + name(nbt['Inventory'][0]);
} else {
    return translate('hud.msg.Thaumcraft.BorePick') + ': ' + name(nbt['Inventory'][1]);
}

var result = '';
switch (nbt['type']) {
    case 0:
        result = translate('nodetype.NORMAL.name');
        break;
    case 1:
        result = translate('nodetype.UNSTABLE.name');
        break;
    case 2:
        result = translate('nodetype.DARK.name');
        break;
    case 3:
        result = translate('nodetype.TAINTED.name');
        break;
    case 4:
        result = translate('nodetype.HUNGRY.name');
        break;
    case 5:
        result = translate('nodetype.PURE.name');
        break;
}
result += '';
switch (nbt['modifier']) {
    case 0:
        result += ', ' + translate('nodemod.BRIGHT.name');
        break;
    case 1:
        result += ', ' + translate('nodemod.PALE.name');
        break;
    case 2:
        result += ', ' + translate('nodemod.FADING.name');
        break;
}
return result;

var aspectsInfo = ['\\n'];
for (var i in nbt['AspectsBase']) {
    var aspectBase = nbt['AspectsBase'][i];
    var aspectName = aspectBase['key'];
    var amountBase = aspectBase['amount'];
    var amount = -1;
    for (var j in nbt['Aspects']) {
        var aspect = nbt['Aspects'][j];
        if (aspect['key'] == aspectName) {
            amount = aspect['amount'];
            break;
        }
    }
    aspectsInfo.push(
        '    ' + GREEN + translate('tc.aspect.' + aspectName) + ': ' + WHITE + amount + ' / ' + amountBase
    );
}
return aspectsInfo.join('\\n');
