tileentity.line:
var part = ['\\n'];
var directions = [
    translate('hud.msg.common.Bottom'),
    translate('hud.msg.common.Top'),
    translate('hud.msg.common.North'),
    translate('hud.msg.common.South'),
    translate('hud.msg.common.West'),
    translate('hud.msg.common.East')
];
for (i = 0; i < 6; i++) {
    if (nbt['def:' + i]) {
        var info = '  ' + GREEN + directions[i] + ': ' + WHITE + name(nbt['def:' + i]);
        if (nbt['extra:' + i]['priority'] != undefined) {
            info += '\\n    ' + GREEN + translate('gui.appliedenergistics2.Priority') + ': ';
            info += WHITE + nbt['extra:' + i]['priority'];
        }
        part.push(info);
    }
}
return part.join('\\n');
