tileentity.line:
var results = ['\\n'];
var directions = [
    translate('hud.msg.common.Bottom'),
    translate('hud.msg.common.Top'),
    translate('hud.msg.common.North'),
    translate('hud.msg.common.South'),
    translate('hud.msg.common.West'),
    translate('hud.msg.common.East')
];
for (i = 0; i < 6; i++) {
    if (nbt['def:' + i] != undefined) {
        nbt.push(customLabelFormat(directions[i], name(nbt['def:' + i])));
        if (nbt['extra:' + i]['priority'] != undefined) {
            results.push(
                '  └─' +
                    GREEN +
                    translate('gui.appliedenergistics2.Priority') +
                    ': ' +
                    WHITE +
                    nbt['extra:' + i]['priority']
            );
        }
    }
}
return results.join('\\n  ');
